export function UpdateStates() {
    const navigate = useNavigate();
    const { id } = useParams();

    const [inmuebles, setInmuebles] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:3001/verInmueble/${id}`, {
            headers: {
                'Content-Type': 'application/json'
            }
        }).then((response) => {
            console.log(response.data.rows);
            setInmuebles(response.data.rows);
        }).catch((error) => {
            console.error("Error", error);
        });
    }, [id]);

    const handleChange = (index, value) => {
        setInmuebles((prevInmuebles) => {
            const newInmuebles = [...prevInmuebles];
            newInmuebles[index] = value;
            return newInmuebles;
        });
    };

    async function handleRegister(e) {
        e.preventDefault();

        // ... (resto del código)

        formData.append("barrio", inmuebles[0]);
        formData.append("direccion", inmuebles[1]);
        formData.append("areaConstruida", inmuebles[2]);
        formData.append("areaLote", inmuebles[3]);
        formData.append("estadoConstruccion", inmuebles[4]);
        formData.append("numPisos", inmuebles[5]);
        formData.append("numHabitaciones", inmuebles[6]);
        formData.append("precio", inmuebles[7]);
        formData.append("numBanos", inmuebles[8]);
        formData.append("tipoInmueble", inmuebles[9]);
        formData.append("images", imagesRef.current.files[0]);

        // ... (resto del código)
    }

    return (
        <div>
            <div className="information-header">
                <h2 className="title-header">Nuevo Inmueble</h2>
            </div>

            <form className="form-state" onSubmit={handleRegister} method="POST">
                <div className="publish-box">
                    {["barrio", "direccion", "areaConstruida", "areaLote", "estadoConstruccion", "numPisos", "numHabitaciones", "precio", "numBaños", "tipoInmueble"].map((campo, index) => (
                        <InputForm
                            key={campo}
                            type={index === 5 || index === 6 ? "number" : "text"}
                            options={`${campo.charAt(0).toUpperCase() + campo.slice(1)}: `}
                            value={inmuebles[index] || ''}
                            onChange={(e) => handleChange(index, e.target.value)}
                            placeholder={`Ingrese ${campo}`}
                        />
                    ))}
                    {/* ... (resto del código) */}
                </div>
                <div className="buttons-box">
                    <input type="file" name="images" accept="image/*" multiple ref={imagesRef} />
                    <button type="submit" className="button-submit">
                        Publicar
                    </button>
                </div>
            </form>
        </div>
    );
}
