import "../../../css/Admin/user-profile.css";
import { ButtonProfile } from "../../client/profile/Profile";
import React, { useEffect, useState } from 'react';
import { useParams} from 'react-router-dom';

export function UsersProfile() {
    
    const { id } = useParams();
    const [user, setUser] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:3001/consultUser/${id}`)
            .then(response => response.json())
            .then(response => {
                setUser(response);
            })
            .catch(error => {
                console.error('Error:', error);
                setUser(null);
            });
    }, [id]);

    return (
        <div className="Principal-content">
                <div key={user[0].idUsuario} className="profile-info-box">
                    <div className="profile-info-img">
                        <img src="./../../../img/icons/user-profile-2.png"></img>
                    </div>
                    <div className="profile-info-data">
                        <p className="txt-black"><p className="info-user-title">Estado:</p> {user[0].estado === 1 ? "activo" : "inactivo"}</p>
                        <p className="txt-black"><p className="info-user-title">Usuario:</p>{user[0].numIdentificacion}</p>
                    </div>
                </div>
                <div className="profile-data-box">
                    <div className="title">
                        <p className="txt-black">Datos de la cuenta</p>
                    </div>

                    {/* Caja de datos */}
                    <div className="data-box">
                        <p className="txt-black"><p className="info-user-title">Tipo de Identificación:</p> {user[0].tipoIdentificacion}</p>
                        <p className="txt-black"><p className="info-user-title">Nombres:</p> {user[0].nombre}</p>
                        <p className="txt-black"><p className="info-user-title">Apellidos:</p> {user[0].tipoIdentificacion}</p>
                        <p className="txt-black"><p className="info-user-title">Correo:</p> {user[0].tipoIdentificacion}</p>
                        <p className="txt-black"><p className="info-user-title">Numero de telefono:</p> {user[0].tipoIdentificacion}</p>
                    </div>

                    {/* Botones de acción */}
                    <div className="button-box">
                        <ButtonProfile props="Inactivar Cuenta" />
                    </div>
                </div>
            </div>
    )
}
