import "../../../css/Advisory/newstate.css";
import { InputForm } from "../../admin/state/NewState";
import { useNavigate, useParams } from "react-router-dom";
import React, { useRef, useEffect, useState } from "react";
import axios from "axios";

export function UpdateStates() {
    const navigate = useNavigate();
    const { id } = useParams();

    const [inmuebles, setInmuebles] = useState({});
    
    const [formValues, setFormValues] = useState({
        barrio: '',
        direccion: '',
        areaConstruida: '',
        areaTerreno: '',
        estadoConstruccion: '',
        numPisos: 0,
        numHabitaciones: 0,
        precio: 0,
        numBaños: 0,
        tipoInmueble: 'casa',  // Valor predeterminado, ajusta según tus necesidades
    });

    useEffect(() => {
        // Cuando inmuebles cambia, actualiza el valor del estado local.
        if (inmuebles) {
            setFormValues({
                barrio: inmuebles.barrio || '',
                direccion: inmuebles.direccion || '',
                areaConstruida: inmuebles.areaConstruida || '',
                areaTerreno: inmuebles.areaTerreno || '',
                estadoConstruccion: inmuebles.estadoConstruccion || '',
                numPisos: inmuebles.numPisos || 0,
                numHabitaciones: inmuebles.numHabitaciones || 0,
                precio: inmuebles.precio || 0,
                numBaños: inmuebles.numBaños || 0,
                tipoInmueble: inmuebles.tipoInmueble || 'casa',  // Valor predeterminado, ajusta según tus necesidades
            });
        }
    }, [inmuebles]);

    const barrioRef = useRef(null);
    const direccionRef = useRef(null);
    const areaConstruidaRef = useRef(null);
    const areaLoteRef = useRef(null);
    const estadoConstruccionRef = useRef(null);
    const numPisosRef = useRef(null);
    const numHabitacionesRef = useRef(null);
    const precioRef = useRef(null);
    const numBanosRef = useRef(null);
    const tipoInmuebleRef = useRef(null);
    const imagesRef = useRef(null);

    const handleInputChange = (field, value) => {
        setFormValues({
            ...formValues,
            [field]: value,
        });
    };

    async function handleRegister(e) {
        e.preventDefault();

        const formData = new FormData();
        formData.append("barrio", formValues.barrio);
        formData.append("direccion", formValues.direccion);
        formData.append("areaConstruida", formValues.areaConstruida);
        formData.append("areaLote", formValues.areaTerreno);
        formData.append("estadoConstruccion", formValues.estadoConstruccion);
        formData.append("numPisos", formValues.numPisos);
        formData.append("numHabitaciones", formValues.numHabitaciones);
        formData.append("precio", formValues.precio);
        formData.append("numBanos", formValues.numBaños);
        formData.append("tipoInmueble", formValues.tipoInmueble);
        formData.append("images", imagesRef.current.files[0]);

        axios.patch(`http://localhost:3001/actualizarInmueble/${id}`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        }).then((data) => {
            if (data) {
                console.log("Registro exitoso", data);
                alert("Registro exitoso");
            }
        }).catch((error) => {
            console.error("Error", error);
            alert("Error en la solicitud: " + error.message);
        });
    }

    return (
        <div>
            <div className="information-header">
                <h2 className="title-header">Nuevo Inmueble</h2>
            </div>
            <form className="form-state" onSubmit={handleRegister} method="POST">
                <div className="publish-box">
                    <InputForm type="text" options="Barrio: " value={formValues.barrio} onChange={(e) => handleInputChange('barrio', e.target.value)} placeholder="Ingrese el nombre del barrio" refe={barrioRef} />
                    <InputForm type="text" options="Dirección: " value={formValues.direccion} onChange={(e) => handleInputChange('direccion', e.target.value)} placeholder="Ingrese la dirección" refe={direccionRef} />
                    <InputForm type="text" options="Área Construida: " value={formValues.areaConstruida} onChange={(e) => handleInputChange('areaConstruida', e.target.value)} placeholder="Ingrese el área construida" refe={areaConstruidaRef} />
                    <InputForm type="text" options="Área Terreno: " value={formValues.areaTerreno} onChange={(e) => handleInputChange('areaTerreno', e.target.value)} placeholder="Ingrese el área del terreno" refe={areaLoteRef} />
                    <InputForm type="text" options="Estado Construcción: " value={formValues.estadoConstruccion} onChange={(e) => handleInputChange('estadoConstruccion', e.target.value)} placeholder="Ingrese el estado de construcción" refe={estadoConstruccionRef} />
                    <InputForm type="number" options="Número de pisos: " value={formValues.numPisos} onChange={(e) => handleInputChange('numPisos', e.target.value)} placeholder="Ingrese el número de pisos" refe={numPisosRef} />
                    <InputForm type="number" options="Número de habitaciones " value={formValues.numHabitaciones} onChange={(e) => handleInputChange('numHabitaciones', e.target.value)} placeholder="Ingrese el número de habitaciones" refe={numHabitacionesRef} />
                    <InputForm type="number" options="Precio " value={formValues.precio} onChange={(e) => handleInputChange('precio', e.target.value)} placeholder="Ingrese el precio del inmueble" refe={precioRef} />
                    <InputForm type="number" options="Número de Baños: " value={formValues.numBaños} onChange={(e) => handleInputChange('numBaños', e.target.value)} placeholder="Ingrese el número de baños" refe={numBanosRef} />
                    <div className="subdiv-publish-box ">
                        <p className="name-option">Tipo de Inmueble:</p>
                        <select id="tipoInmueble" name="tipoInmueble" value={formValues.tipoInmueble} onChange={(e) => handleInputChange('tipoInmueble', e.target.value)} className="select-type-state" ref={tipoInmuebleRef}>
                            <option value="casa">Casa</option>
                            <option value="apartamento">Apartamento</option>
                            <option value="lote">Lote</option>
                            <option value="casaLote">Casa Lote</option>
                        </select>
                    </div>
                </div>
                <div className="buttons-box">
                    <input type="file" name="images" accept="image/*" multiple ref={imagesRef} />
                    <button type="submit" className="button-submit">
                        Publicar
                    </button>
                </div>
            </form>
        </div>
    );
}
