import "../../../css/Advisory/newstate.css";
import { InputForm } from "../../admin/state/NewState";
import { useNavigate, useParams } from "react-router-dom";
import React, { useRef, useEffect, useState } from "react";
import axios from "axios";


export function UpdateStates() {

    const navigate = useNavigate();

 

    const {id} = useParams();

    const barrioRef = useRef(null);
    const direccionRef = useRef(null);
    const areaConstruidaRef = useRef(null);
    const areaLoteRef = useRef(null);
    const estadoConstruccionRef = useRef(null);
    const numPisosRef = useRef(null);
    const numHabitacionesRef = useRef(null);
    const precioRef = useRef(null);
    const numBanosRef = useRef(null);
    const tipoInmuebleRef = useRef(null);
    const imagesRef = useRef(null);


    useEffect(() => {
        axios.get(`http://localhost:3001/verInmueble/${id}`)
            .then(response => {
                console.log(response.data.rows);
                setState(response.data.rows);
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }, []);

    console.log(state.barrio)


    async function handleRegister(e) {
        e.preventDefault();
        
        const formData = new FormData();
        formData.append("barrio", barrioRef.current.value);
        formData.append("direccion", direccionRef.current.value);
        formData.append("areaConstruida", areaConstruidaRef.current.value);
        formData.append("areaLote", areaLoteRef.current.value);
        formData.append("estadoConstruccion", estadoConstruccionRef.current.value);
        formData.append("numPisos", numPisosRef.current.value);
        formData.append("numHabitaciones", numHabitacionesRef.current.value);
        formData.append("precio", precioRef.current.value);
        formData.append("numBanos", numBanosRef.current.value);
        formData.append("tipoInmueble", tipoInmuebleRef.current.value);
        formData.append("images", imagesRef.current.files[0]);

        fetch("/actualizarInmueble/${id}", {
            method: "POST",
            body: formData,
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then((data) => {
                console.log("Registro exitoso", data);
                alert("Registro exitoso");
                navigate(`/advisory/propierty-list`);
            })
            .catch((error) => {
                console.error("Error", error);
                alert("Error en la solicitud: " + error.message);
            });
    };

    return (
        <div>
            <div className="information-header">
                <h2 className="title-header">Nuevo Inmueble</h2>
            </div>
            
                <form className="form-state" onSubmit={handleRegister} method="POST">
                    
                <div className="publish-box">
                    <InputForm type="text" options="Barrio: "  value={state?.barrio} placeholder="Ingrese el nombre del barrio" refe={barrioRef} />
                    <InputForm type="text" options="Direcci√≥n: " value={state?.direccion} placeholder="Ingrese la direcci√≥n" refe={direccionRef} />
                    <InputForm type="text" options="√Årea Construida: " value={state?.areaConstruida} placeholder="Ingrese el √°rea construida (ej: 6 x 12)" refe={areaConstruidaRef} />
                    <InputForm type="text" options="√Årea Terreno: " value={state?.areaTerreno}  placeholder="Ingrese el √°rea del terreno" refe={areaLoteRef} />
                    <InputForm type="text" options="Estado Construcci√≥n: " value={state?.estadoConstruccion} placeholder="Ingrese el estado de construcci√≥n (ej: Terminada)" refe={estadoConstruccionRef} />
                    <InputForm type="number" options="N√∫mero de pisos: " value={state?.numPisos} placeholder="Ingrese el n√∫mero de pisos" refe={numPisosRef} />
                    <InputForm type="number" options="N√∫mero de habitaciones " value={state?.numHabitaciones} placeholder="Ingrese el n√∫mero de habitaciones" refe={numHabitacionesRef} />
                    <InputForm type="number" options="Precio " value={state?.precio} placeholder="Ingrese el precio del inmueble" refe={precioRef} />
                    <InputForm type="number" options="N√∫mero de Ba√±os: " value={state?.numBa√±os} placeholder="Ingrese el n√∫mero de ba√±os" refe={numBanosRef} />
                    <div className="subdiv-publish-box ">
                        <p className="name-option">Tipo de Inmueble:</p>
                        <select id="tipoInmueble" name="tipoInmueble" value={state?.tipoInmueble} className="select-type-state" ref={tipoInmuebleRef}>
                            <option value="casa">Casa</option>
                            <option value="apartamento">Apartamento</option>
                            <option value="lote">Lote</option>
                            <option value="casaLote">Casa Lote</option>
                        </select>
                    </div>
                </div>
                <div className="buttons-box">
                    <input type="file" name="images" accept="image/*" value={state?.imagenes} multiple ref={imagesRef} />
                    <button type="submit" className="button-submit">
                        Publicar
                    </button>
                </div>
            </form>
            
        </div>
    );
}
