import "../../../css/Advisory/newstate.css";
import { InputForm } from "../../admin/state/NewState";
import { useNavigate, useParams } from "react-router-dom";
import React, { useRef, useEffect, useState } from "react";
import axios from "axios";

export function UpdateStates() {
    const navigate = useNavigate();
    const { id } = useParams();

    const [inmuebles, setInmuebles] = useState({});

    useEffect(() => {
        axios.get(`http://localhost:3001/verInmueble/${id}`, {
            headers: {
                'Content-Type': 'application/json'
            }
        }).then((response) => {
            console.log(response.data.rows);
            setInmuebles(response.data.rows);
        }).catch((error) => {
            console.error("Error", error);
        });
    }, [id]);

    const barrioRef = useRef(null);
    const direccionRef = useRef(null);
    const areaConstruidaRef = useRef(null);
    const areaLoteRef = useRef(null);
    const estadoConstruccionRef = useRef(null);
    const numPisosRef = useRef(null);
    const numHabitacionesRef = useRef(null);
    const precioRef = useRef(null);
    const numBanosRef = useRef(null);
    const tipoInmuebleRef = useRef(null);
    const imagesRef = useRef(null);

    const handleInputChange = (field, value) => {
        setInmuebles((prevInmuebles) => ({
            ...prevInmuebles,
            [field]: value,
        }));
    };

    async function handleRegister(e) {
        e.preventDefault();

        const formData = new FormData();
        formData.append("barrio", barrioRef.current.value);
        formData.append("direccion", direccionRef.current.value);
        formData.append("areaConstruida", areaConstruidaRef.current.value);
        formData.append("areaLote", areaLoteRef.current.value);
        formData.append("estadoConstruccion", estadoConstruccionRef.current.value);
        formData.append("numPisos", numPisosRef.current.value);
        formData.append("numHabitaciones", numHabitacionesRef.current.value);
        formData.append("precio", precioRef.current.value);
        formData.append("numBanos", numBanosRef.current.value);
        formData.append("tipoInmueble", tipoInmuebleRef.current.value);
        formData.append("images", imagesRef.current.files[0]);

        axios.patch(`http://localhost:3001/actualizarInmueble/${id}`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        }).then((data) => {
            if (data) {
                console.log("Registro exitoso", data);
                alert("Registro exitoso");
            }
        }).catch((error) => {
            console.error("Error", error);
            alert("Error en la solicitud: " + error.message);
        });
    }

    return (
        <div>
            <div className="information-header">
                <h2 className="title-header">Nuevo Inmueble</h2>
            </div>
            
            <form className="form-state" onSubmit={handleRegister} method="POST">
                <div className="publish-box">
                    <InputForm type="text" options="Barrio: " value={inmuebles.barrio || ''} onChange={(e) => handleInputChange('barrio', e.target.value)} placeholder="Ingrese el nombre del barrio" ref={barrioRef} />
                    <InputForm type="text" options="Dirección: " value={inmuebles.direccion || ''} onChange={(e) => handleInputChange('direccion', e.target.value)} placeholder="Ingrese la dirección" ref={direccionRef} />
                    <InputForm type="text" options="Área Construida: " value={inmuebles.areaConstruida || ''} onChange={(e) => handleInputChange('areaConstruida', e.target.value)} placeholder="Ingrese el área construida" ref={areaConstruidaRef} />
                    <InputForm type="text" options="Área Terreno: " value={inmuebles.areaTerreno || ''} onChange={(e) => handleInputChange('areaTerreno', e.target.value)} placeholder="Ingrese el área del terreno" ref={areaLoteRef} />
                    <InputForm type="text" options="Estado Construcción: " value={inmuebles.estadoConstruccion || ''} onChange={(e) => handleInputChange('estadoConstruccion', e.target.value)} placeholder="Ingrese el estado de construcción" ref={estadoConstruccionRef} />
                    <InputForm type="number" options="Número de pisos: " value={inmuebles.numPisos || 0} onChange={(e) => handleInputChange('numPisos', e.target.value)} placeholder="Ingrese el número de pisos" ref={numPisosRef} />
                    <InputForm type="number" options="Número de habitaciones " value={inmuebles.numHabitaciones || 0} onChange={(e) => handleInputChange('numHabitaciones', e.target.value)} placeholder="Ingrese el número de habitaciones" ref={numHabitacionesRef} />
                    <InputForm type="number" options="Precio " value={inmuebles.precio || 0} onChange={(e) => handleInputChange('precio', e.target.value)} placeholder="Ingrese el precio del inmueble" ref={precioRef} />
                    <InputForm type="number" options="Número de Baños: " value={inmuebles.numBaños || 0} onChange={(e) => handleInputChange('numBaños', e.target.value)} placeholder="Ingrese el número de baños" ref={numBanosRef} />
                    <div className="subdiv-publish-box ">
                        <p className="name-option">Tipo de Inmueble:</p>
                        <select id="tipoInmueble" name="tipoInmueble" value={inmuebles.tipoInmueble || 'casa'} onChange={(e) => handleInputChange('tipoInmueble', e.target.value)} className="select-type-state" ref={tipoInmuebleRef}>
                            <option value="casa">Casa</option>
                            <option value="apartamento">Apartamento</option>
                            <option value="lote">Lote</option>
                            <option value="casaLote">Casa Lote</option>
                        </select>
                    </div>
                </div>
                <div className="buttons-box">
                    <input type="file" name="images" accept="image/*" multiple ref={imagesRef} />
                    <button type="submit" className="button-submit">
                        Publicar
                    </button>
                </div>
            </form>
        </div>
    );
}
